import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [products, setProducts] = useState([]);
  const [order, setOrder] = useState({ product_id: "", quantity: "" });
  const [message, setMessage] = useState("");

  // Fetch products from the backend
  useEffect(() => {
    axios
      .get("http://localhost:5000/products") // Replace with your backend API endpoint
      .then((response) => {
        setProducts(response.data);
      })
      .catch((error) => {
        console.error("Error fetching products:", error);
      });
  }, []);

  // Handle order submission
  const handleOrder = () => {
    if (!order.product_id || !order.quantity) {
      setMessage("Please select a product and quantity.");
      return;
    }

    axios
      .post("http://localhost:5000/orders", order) // Replace with your backend API endpoint
      .then((response) => {
        setMessage(`Order placed successfully! Order ID: ${response.data.orderId}`);
      })
      .catch((error) => {
        console.error("Error placing order:", error);
        setMessage("Failed to place order.");
      });
  };

  return (
    <div className="app-container">
      <header>
        <h1>E-Commerce App</h1>
      </header>

      <section className="product-list">
        <h2>Products</h2>
        <div className="products">
          {products.map((product) => (
            <div key={product.id} className="product-card">
              <h3>{product.name}</h3>
              <p>${product.price.toFixed(2)}</p>
              <button onClick={() => setOrder({ ...order, product_id: product.id })}>
                Select
              </button>
            </div>
          ))}
        </div>
      </section>

      <section className="order-form">
        <h2>Place Order</h2>
        <div>
          <input
            type="number"
            placeholder="Quantity"
            value={order.quantity}
            onChange={(e) => setOrder({ ...order, quantity: e.target.value })}
          />
          <button onClick={handleOrder}>Submit Order</button>
        </div>
        {message && <p className="message">{message}</p>}
      </section>
    </div>
  );
}

export default App;
